import React, { useState } from "react";
import "./GameCard.scss";
import playerIcon from "../../assets/icons/player-icon.svg";

const GameCard = ({ gameData }) => {
	const [showText, setShowText] = useState(false);
	const handleMouseEnter = (e) => {
		// e.target.style.background = "yellow";
		setShowText(true);
	};
	const handleMouseLeave = (e) => {
		e.target.style.background = "maroon";
		setShowText(false);
	};

	return (
		<section className="games-container">
			{showText && <div className="overlay">Join</div>}
			{gameData.map((game) => {
				// {
				// 	console.log(game);
				// }
				return (
					<>
						<article
							className="game-card"
							key={game.id}
							onMouseEnter={handleMouseEnter}>
							<img
								src={game.image}
								alt={`${game.title} Logo`}
								className="game-card__image"
							/>
							<div className="game-card__info-container">
								<p className="game-card__genre">
									{game.genre}helo
								</p>
								<h4 className="game-card__title">
									{game.title}
								</h4>
								<div className="game-card__info">
									<p className="game-card__time">
										{game.time} mins
									</p>
									<p className="game-card__players">
										<img
											src={playerIcon}
											alt="human icon"
											className="game-card__player-icon"
										/>{" "}
										{game.players}+
									</p>
								</div>
							</div>
						</article>
					</>
				);
			})}
		</section>
	);
};

export default GameCard;
