import React from "react";
import "./GameCard.scss";
import playerIcon from "../../assets/icons/player-icon.svg";
import cardsAgainstHumanity from "../../assets/images/cards-against-humanity.png";

const GameCard = ({ gameData }) => {
	return (
		<article className="game-card">
			<img
				src={gameData.image}
				alt={`{gameData.title} Logo`}
				className="game-card__image"
			/>
			<div className="game-card__info-container">
				<p className="game-card__genre">{gameData.genre}</p>
				<h4 className="game-card__title">{gameData.title}</h4>
				<div className="game-card__info">
					<p className="game-card__time">{gameData.time} mins</p>
					<p className="game-card__players">
						<img
							src={playerIcon}
							alt="human icon"
							className="game-card__player-icon"
						/>{" "}
						{gameData.players}+
					</p>
				</div>
			</div>
		</article>
	);
};

export default GameCard;
